.ripple {
  position: relative;
  overflow: hidden;
}
.ripple::after {
    content: '';
    display: block;
    position: absolute;
    margin: 0;
    padding: 0;
    left: 50%;
    top: 50%;
    width: 120px;
    height: 120px;
    background-color: #f5f5f5;
    border-radius: 50%;
    opacity: .2;
    -webkit-transform: translate(-50%, -50%) scale(0);
            transform: translate(-50%, -50%) scale(0);
}
.ripple:not(:active):after {
    -webkit-animation: ripple-effect 1s ease-out;
            animation: ripple-effect 1s ease-out;
}
.ripple::after {
    visibility: hidden;
}
.ripple:focus::after {
    visibility: visible;
}
.ripple-dark::after {
    background-color: #ddd;
}
.button-disabled {
  opacity: 0.3;
  pointer-events: none;
}
button,
.button {
  outline: none;
  cursor: pointer;
  margin: 0;
  -webkit-tap-highlight-color: rgba(0, 0, 0, 0);
  -webkit-tap-highlight-color: transparent;
}
.sagePay {
