  text-align: justify;
}
.weight-400 {
  font-weight: 400;
}
.weight-700 {
  font-weight: 700;
}
.lh16 {
  line-height: 16px;
}
.lh20 {
  line-height: 20px;
}
.lh25 {
  line-height: 25px;
}
.lh30 {
  line-height: 30px;
}
.lh35 {
  line-height: 35px;
}
.lh40 {
  line-height: 40px;
}
.fs-medium-small {
  font-size: 14px;
}
.fs-medium {
  font-size: 18px;
}
.fs-large {
  font-size: 24px;
}
.fs-big {
  font-size: 36px;
}
.fs16 {
  font-size: 16px;
}
#checkout .number-circle {
  width: 35px;
  height: 35px;
}
@media (max-width: 768px) {
#checkout .number-circle {
      width: 25px;
      height: 25px;
      line-height: 25px;
}
}
#checkout .radioStyled {
  display: block;
  position: relative;
  padding-left: 35px;
  margin-bottom: 12px;
  cursor: pointer;
  font-size: 16px;
  line-height: 30px;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
}
#checkout .radioStyled input {
    position: absolute;
    opacity: 0;
    cursor: pointer;
}
#checkout .radioStyled .checkmark {
    position: absolute;
    top: 0;
    left: 0;
    height: 25px;
    width: 25px;
    border-radius: 50%;
    border: 1px solid #f2f2f2;
}
#checkout .radioStyled .checkmark:after {
      content: "";
      position: absolute;
      display: none;
      top: 3px;
      left: 3px;
      width: 19px;
      height: 19px;
      border-radius: 50%;
      background: #828282;
}
#checkout .radioStyled input:checked ~ .checkmark:after {
    display: block;
}
.line:after {
  content: '';
  display: block;
  position: absolute;
  top: 0;
  left: 37px;
  z-index: -1;
  width: 1px;
  height: 100%;
  background-color: #f2f2f2;
}
@media (max-width: 768px) {
.line:after {
      display: none;
}
}
@media (max-width: 767px) {
.checkout-title {
    background-color: #f2f2f2;
    margin-bottom: 25px;
}
.checkout-title h1 {
      font-size: 36px;
}
}
